import React, { useState } from 'react';

const FAQ = () => {
    const [expanded, setExpanded] = useState(null);

    const questions = [
        {
            id: 1,
            question: '¿Cuál es el primer paso?',
            answer: 'El primer paso es...'
        },
        {
            id: 2,
            question: '¿Cómo puedo hacer esto?',
            answer: 'Puedes hacer esto...'
        },
        // Agrega más preguntas y respuestas aquí
    ];

    const toggleQuestion = (id) => {
        if (expanded === id) {
            setExpanded(null);
        } else {
            setExpanded(id);
        }
    };

    return (
        <div className="max-w-lg mx-auto">
            {questions.map((item) => (
                <div key={item.id} className='bg-slate-900'>
                    <button
                        className="flex items-center justify-between w-full py-2 px-4 bg-slate-900 text-left"
                        onClick={() => toggleQuestion(item.id)}
                    >
                        <span className="font-medium ">{item.question}</span>
                        <svg
                            className={`w-4 h-4 transition-transform transform ${expanded === item.id ? 'rotate-180' : 'rotate-0'
                                }`}
                            viewBox="0 0 24 24"
                            fill="none"
                            stroke="currentColor"
                            strokeWidth="2"
                            strokeLinecap="round"
                            strokeLinejoin="round"
                        >
                            <polyline points="6 9 12 15 18 9" />
                        </svg>
                    </button>
                    {expanded === item.id && (
                        <div className="py-2 px-4 bg-slate-900">{item.answer}</div>
                    )}
                </div>
            ))}
        </div>
    );
};

export default FAQ;
