import React, { useState } from 'react';

const FAQ = () => {
    const [activeAccordion, setActiveAccordion] = useState(null);

    const handleAccordionClick = (accordionId) => {
        if (activeAccordion === accordionId) {
            setActiveAccordion(null);
        } else {
            setActiveAccordion(accordionId);
        }
    };

    return (
        <div className="p-4 md:p-8 bg-slate-900" id="faq" role="tabpanel" aria-labelledby="faq-tab">
            <h3 className='text-center text-5xl text-white font-extrabold py-8'>Preguntas frecuentes</h3>
            <div
                id="accordion-flush"
                data-accordion="collapse"
                data-active-classes="bg-white dark:bg-gray-800 text-gray-900 dark:text-white"
                data-inactive-classes="text-gray-500 dark:text-gray-400"
            >
                <h2 id="accordion-flush-heading-1">
                    <button
                        type="button"
                        className={`flex items-center justify-between w-full py-5 font-medium text-left text-gray-500 border-b border-gray-200 dark:border-gray-700 dark:text-gray-400 ${activeAccordion === 'accordion-flush-body-1' ? 'bg-blue-100 dark:bg-blue-900' : ''
                            }`}
                        onClick={() => handleAccordionClick('accordion-flush-body-1')}
                        aria-expanded={activeAccordion === 'accordion-flush-body-1' ? 'true' : 'false'}
                        aria-controls="accordion-flush-body-1"
                    >
                        <span>What is Flowbite?</span>
                        <svg
                            data-accordion-icon
                            className={`w-6 h-6 rotate-180 shrink-0 ${activeAccordion === 'accordion-flush-body-1' ? 'transform rotate-0' : ''
                                }`}
                            fill="currentColor"
                            viewBox="0 0 20 20"
                            xmlns="http://www.w3.org/2000/svg"
                        >
                            <path
                                fillRule="evenodd"
                                d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z"
                                clipRule="evenodd"
                            />
                        </svg>
                    </button>
                </h2>
                <div
                    id="accordion-flush-body-1"
                    className={`hidden ${activeAccordion === 'accordion-flush-body-1' ? 'block' : ''
                        }`}
                    aria-labelledby="accordion-flush-heading-1"
                >
                    <div className="py-5 border-b border-gray-200 dark:border-gray-700">
                        <p className="mb-2 text-gray-500 dark:text-gray-400">
                            Flowbite is an open-source library of interactive components built on top of Tailwind CSS including buttons,
                            dropdowns, modals, navbars, and more.
                        </p>
                        <p className="text-gray-500 dark:text-gray-400">
                            Check out this guide to learn how to{' '}
                            <a href="/docs/getting-started/introduction/" className="text-blue-600 dark:text-blue-500 hover:underline">
                                get started
                            </a>{' '}
                            and start developing websites even faster with components on top of Tailwind CSS.
                        </p>
                    </div>
                </div>
                {/* Resto del c√≥digo de los desplegables */}
            </div>
        </div>
    );
};

export default FAQ;
